
<?php namespace Phppot; class Member { private $ds; function __construct() { require_once __DIR__ . '/../lib/DataSource.php'; $this->ds = new DataSource(); } /** * to check if the username already exists * * @param string $username * @return boolean */ public function isUsernameExists($username) { $query = 'SELECT * FROM tbl_member where username = ?'; $paramType = 's'; $paramValue = array( $username ); $resultArray = $this->ds->select($query, $paramType, $paramValue); $count = 0; if (is_array($resultArray)) { $count = count($resultArray); } if ($count > 0) { $result = true; } else { $result = false; } return $result; } /**